mapper.select:
SELECT u.id,u.role_id,u.username,u.password,u.address, u.phone,u.create_user_id,u.create_time,u.user_renew_id,u.renew_time, r.id AS rid,r.username AS rusername,r.user_power,r.parent_id,r.create_user_id,r.create_time AS rcreatetime, r.user_renew_id,r.renew_time AS rrenewtime, p.id AS pid , p.role_id ,p.can_use,p.create_user_id,p.create_time AS pcreatetime,p.user_renew_id, p.renew_time AS prenewtime FROM USER u INNER JOIN role r ON u.role_id=r.id INNER JOIN POWER p ON r.user_power=r.id AND r.id=p.id WHERE r.username='收银员' AND u.con1=1
and u.username like concat ('%',#{username},'%')
and u.address=#{address}
and u.phone=#{phone}

